import styled from "styled-components"

const Container = styled.div`
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 2rem;
	margin-top: -10rem;

	div {
		background: var(--shape);
		padding: 1.5rem 2rem;
		border-radius: 0.25rem;
	}
`

export {
	Container
}
