import styled from 'styled-components';

const Container = styled.div`
  margin-top: 4rem;

  table {
    width: 100%;
    border-spacing: 0 0.5rem;

    th {
      color: var(--text-body);
      font-weight: 500  
    }
  }
`

export {
	Container
}
