import styled from 'styled-components'

const Container = styled.header`
  background: var(--blue);
`
const Content = styled.div`
max-width: 80%;
margin: 0 auto;
padding: 2rem 1rem 12rem;
display: flex;
align-items: center;
justify-content: space-between;

button {
  font-size: 1rem;
  color: var(--shape);
  background: var(--blue-light);
  border: 0;
  padding: 0 2rem;
  border-radius: 0.25rem;
  height: 3rem;

  &:hover {
    filter: blur(0.1rem)
  }
}
`

export {
	Container,
	Content
}
